import React, { useState, useEffect } from 'react';
import { createVendor, fetchProcesses, createVendorProcess } from '../services/api';

const CreateVendor = () => {
  const [vendor, setVendor] = useState({ vendorId: '', contact: '', location: '' });
  const [message, setMessage] = useState('');
  const [processes, setProcesses] = useState([]); // List of available processes
  const [selectedProcesses, setSelectedProcesses] = useState([]); // Selected processes for the vendor

  // Fetch processes on component mount
  useEffect(() => {
    const loadProcesses = async () => {
      try {
        const processList = await fetchProcesses();
        setProcesses(processList);
      } catch (err) {
        console.error('Error fetching processes:', err.message);
      }
    };
    loadProcesses();
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setVendor((prev) => ({ ...prev, [name]: value }));
  };

  const handleProcessSelection = (e) => {
    const { value } = e.target;
    setSelectedProcesses((prev) =>
      prev.includes(value) ? prev.filter((process) => process !== value) : [...prev, value]
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Step 1: Create the vendor
      const createdVendor = await createVendor(vendor);

      // Step 2: Link processes to the vendor
      const vendorProcessPromises = selectedProcesses.map((processId) =>
        createVendorProcess({ vendorId: createdVendor.vendorId, processId })
      );
      await Promise.all(vendorProcessPromises);

      setMessage('Vendor and processes linked successfully!');
      setVendor({ vendorId: '', contact: '', location: '' });
      setSelectedProcesses([]);
    } catch (err) {
      console.error('Error creating vendor or linking processes:', err.message);
      setMessage('Failed to create vendor or link processes.');
    }
  };

  return (
    <div>
      <h1>Create Vendor</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="vendorId"
          placeholder="Vendor ID"
          value={vendor.vendorId}
          onChange={handleChange}
          required
        />
        <input
          type="text"
          name="contact"
          placeholder="Contact"
          value={vendor.contact}
          onChange={handleChange}
        />
        <input
          type="text"
          name="location"
          placeholder="Location"
          value={vendor.location}
          onChange={handleChange}
        />
        <div>
          <h3>Select Processes</h3>
          {processes.map((process) => (
            <div key={process._id}>
              <input
                type="checkbox"
                id={process.processId}
                value={process.processId}
                checked={selectedProcesses.includes(process.processId)}
                onChange={handleProcessSelection}
              />
              <label htmlFor={process.processId}>{process.name}</label>
            </div>
          ))}
        </div>
        <button type="submit">Create Vendor</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default CreateVendor;
