import React, { useState, useEffect } from 'react';
import { fetchVendors, fetchProcesses } from '../services/api';

const EditPopup = ({ lineItem, onClose, onSave }) => {
  const [vendors, setVendors] = useState([]); // Vendor list
  const [processes, setProcesses] = useState([]); // Process list
  const [selectedVendorIds, setSelectedVendorIds] = useState(lineItem.vendorIds || []); // Selected vendors
  const [selectedProcessIds, setSelectedProcessIds] = useState(lineItem.processIds || []); // Selected processes

  // Fetch vendors only when the dropdown is clicked and vendors are not already loaded
  const fetchVendorsOnClick = async () => {
    if (vendors.length > 0) return; // Avoid redundant fetches
    try {
      const vendorData = await fetchVendors();
      setVendors(vendorData);
    } catch (err) {
      console.error('Error fetching vendors:', err.message);
    }
  };

  // Fetch processes only when the dropdown is clicked and processes are not already loaded
  const fetchProcessesOnClick = async () => {
    if (processes.length > 0) return; // Avoid redundant fetches
    try {
      const processData = await fetchProcesses();
      setProcesses(processData);
    } catch (err) {
      console.error('Error fetching processes:', err.message);
    }
  };

  // Handle dropdown changes
  const handleVendorChange = (e) => {
    const value = e.target.value;
    setSelectedVendorIds(value ? [value] : []);
  };

  const handleProcessChange = (e) => {
    const value = e.target.value;
    setSelectedProcessIds(value ? [value] : []);
  };

  // Save the changes
  const handleSave = () => {
    onSave({
      ...lineItem,
      vendorIds: selectedVendorIds,
      processIds: selectedProcessIds,
    });
  };

  return (
    <div className="popup">
      <div className="popup-content">
        <h2>Edit Line Item</h2>

        {/* Vendor Dropdown */}
        <label htmlFor="vendors">Select Vendor:</label>
        <select
          id="vendors"
          value={selectedVendorIds[0] || ''}
          onChange={handleVendorChange}
          onClick={fetchVendorsOnClick} // Fetch vendors when dropdown is clicked
        >
          <option value="" disabled>
            {vendors.length === 0 ? 'Loading vendors...' : 'Select a vendor'}
          </option>
          {vendors.map((vendor) => (
            <option key={vendor.id} value={vendor.vendorId}>
              {vendor.vendorId}
            </option>
          ))}
        </select>

        {/* Process Dropdown */}
        <label htmlFor="processes">Select Process:</label>
        <select
          id="processes"
          value={selectedProcessIds[0] || ''}
          onChange={handleProcessChange}
          onClick={fetchProcessesOnClick} // Fetch processes when dropdown is clicked
        >
          <option value="" disabled>
            {processes.length === 0 ? 'Loading processes...' : 'Select a process'}
          </option>
          {processes.map((process) => (
            <option key={process.id} value={process.processId}>
              {process.processId}
            </option>
          ))}
        </select>

        {/* Save and Cancel Buttons */}
        <button onClick={handleSave}>Save</button>
        <button onClick={onClose}>Cancel</button>
      </div>
    </div>
  );
};

export default EditPopup;
