import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import Select from 'react-select'; // Import react-select for multi-select dropdowns
import {
  fetchOrder,
  fetchLineItems,
  fetchProcesses,
  fetchVendors,
  fetchShapes,
  fetchGrades,
  createLineItem,
} from '../services/api';

const OrderDetails = () => {
  const { id } = useParams();
  const [order, setOrder] = useState(null);
  const [lineItems, setLineItems] = useState([]);
  const [processes, setProcesses] = useState([]);
  const [vendors, setVendors] = useState([]);
  const [shapes, setShapes] = useState([]);
  const [grades, setGrades] = useState([]);
  const [newLineItem, setNewLineItem] = useState({
    lineItemId: '',
    shape: '',
    grade: '',
    dimensions: { length: '', breadth: '', thickness: '', diameter: '' },
    quantity: '',
    weight: '',
    processIds: [],
    vendorIds: [],
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    const loadOrderData = async () => {
      try {
        const orderData = await fetchOrder(id);
        setOrder(orderData);

        const lineItemData = await fetchLineItems(id);
        setLineItems(lineItemData);
      } catch (err) {
        console.error('Error fetching order or line items:', err.message);
      }
    };
    loadOrderData();
  }, [id]);

  const fetchShapesOnClick = async () => {
    if (shapes.length > 0) return;
    try {
      const shapeData = await fetchShapes();
      setShapes(shapeData);
    } catch (err) {
      console.error('Error fetching shapes:', err.message);
    }
  };

  const fetchGradesOnClick = async () => {
    if (grades.length > 0) return;
    try {
      const gradeData = await fetchGrades();
      setGrades(gradeData);
    } catch (err) {
      console.error('Error fetching grades:', err.message);
    }
  };

  const fetchVendorsOnClick = async () => {
    if (vendors.length > 0) return;
    try {
      const vendorData = await fetchVendors();
      setVendors(vendorData);
    } catch (err) {
      console.error('Error fetching vendors:', err.message);
    }
  };

  const fetchProcessesOnClick = async () => {
    if (processes.length > 0) return;
    try {
      const processData = await fetchProcesses();
      setProcesses(processData);
    } catch (err) {
      console.error('Error fetching processes:', err.message);
    }
  };

  const handleShapeChange = (e) => {
    const selectedShape = e.target.value;
    console.log('Shape selected:', selectedShape); // Debug log
    setNewLineItem((prev) => ({ ...prev, shape: selectedShape }));
  };


  const handleDropdownChange = (e) => {
    const { name, value } = e.target;
    setNewLineItem((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createLineItem({ ...newLineItem, orderId: id });
      setMessage('Line item added successfully!');
      setNewLineItem({
        lineItemId: '',
        shape: '',
        grade: '',
        dimensions: { length: '', breadth: '', thickness: '', diameter: '' },
        quantity: '',
        weight: '',
        processIds: [],
        vendorIds: [],
      });
    } catch (err) {
      console.error('Failed to add line item:', err.message);
      setMessage('Failed to add line item.');
    }
  };

  const renderDimensionFields = () => {
    console.log('Selected Shape:', newLineItem.shape); // Debug log

    switch (newLineItem.shape) {
      case 'Rod':
        return (
          <>
            <input
              type="number"
              placeholder="Length"
              value={newLineItem.dimensions.length}
              onChange={(e) =>
                setNewLineItem((prev) => ({
                  ...prev,
                  dimensions: { ...prev.dimensions, length: e.target.value },
                }))
              }
            />
            <input
              type="number"
              placeholder="Diameter"
              value={newLineItem.dimensions.diameter}
              onChange={(e) =>
                setNewLineItem((prev) => ({
                  ...prev,
                  dimensions: { ...prev.dimensions, diameter: e.target.value },
                }))
              }
            />
          </>
        );
      case 'Sheet':
        return (
          <>
            <input
              type="number"
              placeholder="Length"
              value={newLineItem.dimensions.length}
              onChange={(e) =>
                setNewLineItem((prev) => ({
                  ...prev,
                  dimensions: { ...prev.dimensions, length: e.target.value },
                }))
              }
            />
            <input
              type="number"
              placeholder="Breadth"
              value={newLineItem.dimensions.breadth}
              onChange={(e) =>
                setNewLineItem((prev) => ({
                  ...prev,
                  dimensions: { ...prev.dimensions, breadth: e.target.value },
                }))
              }
            />
            <input
              type="number"
              placeholder="Thickness"
              value={newLineItem.dimensions.thickness}
              onChange={(e) =>
                setNewLineItem((prev) => ({
                  ...prev,
                  dimensions: { ...prev.dimensions, thickness: e.target.value },
                }))
              }
            />
          </>
        );
      default:
        return null;
    }
  };


  return (
    <div>
      <h1>Order Details</h1>
      {order && (
        <div>
          <p>Order ID: {order.orderId}</p>
          <p>Creator: {order.creator}</p>
        </div>
      )}

      <h2>Add New Line Item</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Line Item ID"
          value={newLineItem.lineItemId}
          onChange={(e) =>
            setNewLineItem((prev) => ({ ...prev, lineItemId: e.target.value }))
          }
          required
        />
        <select
          name="shape"
          value={newLineItem.shape}
          onChange={handleShapeChange}
          onClick={fetchShapesOnClick}
        >
          <option value="">Select Shape</option>
          {Array.isArray(shapes) && shapes.length > 0 ? (
            shapes.map((shape) => (
              <option key={shape._id} value={shape.shapeId}>
                {shape.shapeId}
              </option>
            ))
          ) : (
            <option disabled>No shapes available</option>
          )}
        </select>

        <select
          name="grade"
          value={newLineItem.grade}
          onChange={handleDropdownChange}
          onClick={fetchGradesOnClick}
        >
          <option value="">Select Grade</option>
          {Array.isArray(grades) && grades.length > 0 ? (
            grades.map((grade) => (
              <option key={grade._id} value={grade.grade}>
                {grade.grade}
              </option>
            ))
          ) : (
            <option disabled>No grades available</option>
          )}
        </select>

        {renderDimensionFields()}

        <select
          name="processId"
          value={newLineItem.processId}
          onChange={handleDropdownChange}
          onClick={fetchProcessesOnClick}
        >
          <option value="">Select Process</option>
          {Array.isArray(processes) && processes.length > 0 ? (
            processes.map((process) => (
              <option key={process._id} value={process.processId}>
                {process.processId}
              </option>
            ))
          ) : (
            <option disabled>No processes available</option>
          )}
        </select>

        <select
          name="vendorId"
          value={newLineItem.vendorId}
          onChange={handleDropdownChange}
          onClick={fetchVendorsOnClick}
        >
          <option value="">Select Vendor</option>
          {Array.isArray(vendors) && vendors.length > 0 ? (
            vendors.map((vendor) => (
              <option key={vendor._id} value={vendor.vendorId}>
                {vendor.vendorId}
              </option>
            ))
          ) : (
            <option disabled>No vendors available</option>
          )}
        </select>

        <button type="submit">Add Line Item</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default OrderDetails;
