import React, { useState, useEffect, useMemo } from 'react';
import { 
  useReactTable, 
  getCoreRowModel, 
  getSortedRowModel, 
  getFilteredRowModel,
  getExpandedRowModel,
  getPaginationRowModel,
  flexRender 
} from '@tanstack/react-table';
import { fetchExecutionDetailsGrouped } from '../services/api';
import ExecutionEditPopup from './ExecutionEditPopup';

const ExecutionDetailsPage = () => {
  const [executions, setExecutions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedExecution, setSelectedExecution] = useState(null);
  const [sorting, setSorting] = useState([]);
  const [groupByProcessVendor, setGroupByProcessVendor] = useState(false);
  const [pageSize, setPageSize] = useState(10); // 🔹 Default 10 rows per page

  useEffect(() => {
    const loadExecutions = async () => {
      try {
        const data = await fetchExecutionDetailsGrouped();
        setExecutions(data);
      } catch (err) {
        console.error('❌ Error fetching execution details:', err);
      } finally {
        setLoading(false);
      }
    };

    loadExecutions();
  }, []);

  const handleOpenPopup = (execution) => setSelectedExecution(execution);
  const handleClosePopup = () => setSelectedExecution(null);
  const toggleGrouping = () => setGroupByProcessVendor(prev => !prev);

  const groupedData = useMemo(() => {
    if (!groupByProcessVendor) return executions;

    const groupedMap = executions.reduce((acc, exec) => {
      const key = `${exec.processInfo?.processId || exec.processId} - ${exec.vendorId}`;

      if (!acc[key]) {
        acc[key] = {
          processVendor: key, 
          processId: exec.processInfo?.processId || exec.processId,
          vendorId: exec.vendorId,
          subRows: [],
          lineItemIds: new Set(),
          orderIds: new Set(),
          statuses: new Set(),
          totalWeightSent: 0,
          totalWeightReceived: 0,
        };
      }

      acc[key].subRows.push(exec);
      acc[key].lineItemIds.add(exec.lineItemId);
      acc[key].orderIds.add(exec.orderId);
      acc[key].statuses.add(exec.status);
      acc[key].totalWeightSent += exec.outWeight;
      acc[key].totalWeightReceived += exec.actualWeightReceived || 0;

      return acc;
    }, {});

    return Object.values(groupedMap).map(group => ({
      ...group,
      lineItemId: [...group.lineItemIds].join(', '),
      orderId: [...group.orderIds].join(', '),
      status: [...group.statuses].join(', '),
      outWeight: group.totalWeightSent,
      actualWeightReceived: group.totalWeightReceived,
    }));
  }, [executions, groupByProcessVendor]);

  const columns = useMemo(() => [
    {
      accessorKey: 'processVendor',
      header: 'Process-Vendor',
      cell: ({ row }) => row.original.processVendor || `${row.original.processId} - ${row.original.vendorId}` || 'N/A',
    },
    { accessorKey: 'lineItemId', header: 'Line Item' },
    { accessorKey: 'orderId', header: 'Order' },
    { accessorKey: 'outWeight', header: 'Weight Sent' },
    { accessorKey: 'actualWeightSent', header: 'Actual Weight Sent' },
    { accessorKey: 'inWeight', header: 'Weight Received' },
    {
      accessorKey: 'actualWeightReceived',
      header: 'Actual Weight Received',
      cell: ({ row }) => row.original.actualWeightReceived ?? 'N/A',
    },
    { accessorKey: 'status', header: 'Status' },
    {
      accessorKey: 'actions',
      header: 'Actions',
      cell: ({ row }) => (
        <button onClick={() => handleOpenPopup(row.original)}>Edit</button>
      ),
    },
  ], []);

  const table = useReactTable({
    data: groupedData,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    getExpandedRowModel: getExpandedRowModel(),
    getPaginationRowModel: getPaginationRowModel(),
    state: { sorting },
    onSortingChange: setSorting,
  });

  return (
    <div className="container">
      <h2>Execution Management</h2>

      {/* Toggle Grouping Button */}
      <button onClick={toggleGrouping} style={{ marginBottom: '10px' }}>
        {groupByProcessVendor ? 'Disable Grouping' : 'Group by Process-Vendor'}
      </button>

      {/* Table */}
      {loading ? (
        <p>Loading execution details...</p>
      ) : (
        <>
          <table border="1">
            <thead>
              {table.getHeaderGroups().map(headerGroup => (
                <tr key={headerGroup.id}>
                  {headerGroup.headers.map(header => (
                    <th key={header.id} onClick={header.column.getToggleSortingHandler()} style={{ cursor: 'pointer' }}>
                      {flexRender(header.column.columnDef.header, header.getContext())}
                      {header.column.getIsSorted() === 'asc' ? ' 🔼' : header.column.getIsSorted() === 'desc' ? ' 🔽' : ''}
                    </th>
                  ))}
                </tr>
              ))}
            </thead>
            <tbody>
              {table.getRowModel().rows.map(row => (
                <>
                  <tr key={row.id} onClick={row.getToggleExpandedHandler()} style={{ fontWeight: row.getIsExpanded() ? 'bold' : 'normal' }}>
                    {row.getVisibleCells().map(cell => (
                      <td key={cell.id}>
                        {flexRender(cell.column.columnDef.cell, cell.getContext())}
                      </td>
                    ))}
                  </tr>

                  {row.getIsExpanded() &&
                    row.original.subRows?.map(subRow => (
                      <tr key={subRow._id} style={{ backgroundColor: '#f9f9f9' }}>
                        <td>{subRow.lineItemId}</td>
                        <td>{subRow.orderId}</td>
                        <td>{subRow.outWeight}</td>
                        <td>{subRow.actualWeightSent}</td>
                        <td>{subRow.inWeight}</td>
                        <td>{subRow.actualWeightReceived ?? 'N/A'}</td>
                        <td>{subRow.status}</td>
                        <td>
                          <button onClick={() => handleOpenPopup(subRow)}>Edit</button>
                        </td>
                      </tr>
                    ))}
                </>
              ))}
            </tbody>
          </table>

          {/* Pagination Controls */}
          <div className="pagination-controls">
            <button onClick={() => table.previousPage()} disabled={!table.getCanPreviousPage()}>
              ◀ Previous
            </button>
            <span> Page {table.getState().pagination.pageIndex + 1} of {table.getPageCount()} </span>
            <button onClick={() => table.nextPage()} disabled={!table.getCanNextPage()}>
              Next ▶
            </button>

            <select value={pageSize} onChange={(e) => setPageSize(Number(e.target.value))}>
              {[10, 20, 50].map(size => (
                <option key={size} value={size}>{size} rows</option>
              ))}
            </select>
          </div>
        </>
      )}

      {/* Popup for Editing */}
      {selectedExecution && (
        <ExecutionEditPopup
          execution={selectedExecution}
          onClose={handleClosePopup}
        />
      )}
    </div>
  );
};

export default ExecutionDetailsPage;

