import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import Select from 'react-select'; // Import react-select for multi-select dropdowns
import {
  fetchOrder,
  fetchLineItems,
  fetchProcesses,
  fetchVendors,
  fetchShapes,
  fetchGrades,
  createLineItem,
} from '../services/api';

const OrderDetails = () => {
  const { id } = useParams();
  const [order, setOrder] = useState(null);
  const [lineItems, setLineItems] = useState([]);
  const [processes, setProcesses] = useState([]);
  const [vendors, setVendors] = useState([]);
  const [shapes, setShapes] = useState([]);
  const [grades, setGrades] = useState([]);
  const [newLineItem, setNewLineItem] = useState({
    lineItemId: '',
    shape: '',
    grade: '',
    dimensions: { length: '', breadth: '', thickness: '', diameter: '' },
    quantity: '',
    weight: '',
    processIds: [], // Updated for multi-select
    vendorIds: [], // Updated for multi-select
  });
  const [message, setMessage] = useState('');

  useEffect(() => {
    const loadOrderData = async () => {
      try {
        const orderData = await fetchOrder(id);
        setOrder(orderData);

        const processData = await fetchProcesses();
        setProcesses(processData);

        const vendorData = await fetchVendors();
        setVendors(vendorData);

        const shapeData = await fetchShapes();
        setShapes(shapeData);

        const gradeData = await fetchGrades();
        setGrades(gradeData);

        const lineItemData = await fetchLineItems(id);
        setLineItems(lineItemData);
      } catch (err) {
        console.error('Error fetching data:', err.message);
      }
    };
    loadOrderData();
  }, [id]);

  const handleDropdownChange = (selectedOption, { name }) => {
    setNewLineItem((prev) => ({
      ...prev,
      [name]: Array.isArray(selectedOption)
        ? selectedOption.map((option) => option.value)
        : selectedOption?.value || '',
    }));
  };

  const handleShapeChange = (e) => {
    const selectedShape = e.target.value;
    setNewLineItem((prev) => ({ ...prev, shape: selectedShape }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createLineItem({ ...newLineItem, orderId: id });
      setMessage('Line item added successfully!');
      setNewLineItem({
        lineItemId: '',
        shape: '',
        grade: '',
        dimensions: { length: '', breadth: '', thickness: '', diameter: '' },
        quantity: '',
        weight: '',
        processIds: [],
        vendorIds: [],
      });
    } catch (err) {
      console.error('Failed to add line item:', err.message);
      setMessage('Failed to add line item.');
    }
  };

  const renderDimensionFields = () => {
    switch (newLineItem.shape) {
      case 'Rod':
        return (
          <>
            <input
              type="number"
              placeholder="Length"
              value={newLineItem.dimensions.length}
              onChange={(e) =>
                setNewLineItem((prev) => ({
                  ...prev,
                  dimensions: { ...prev.dimensions, length: e.target.value },
                }))
              }
            />
            <input
              type="number"
              placeholder="Diameter"
              value={newLineItem.dimensions.diameter}
              onChange={(e) =>
                setNewLineItem((prev) => ({
                  ...prev,
                  dimensions: { ...prev.dimensions, diameter: e.target.value },
                }))
              }
            />
          </>
        );
      case 'Sheet':
        return (
          <>
            <input
              type="number"
              placeholder="Length"
              value={newLineItem.dimensions.length}
              onChange={(e) =>
                setNewLineItem((prev) => ({
                  ...prev,
                  dimensions: { ...prev.dimensions, length: e.target.value },
                }))
              }
            />
            <input
              type="number"
              placeholder="Breadth"
              value={newLineItem.dimensions.breadth}
              onChange={(e) =>
                setNewLineItem((prev) => ({
                  ...prev,
                  dimensions: { ...prev.dimensions, breadth: e.target.value },
                }))
              }
            />
            <input
              type="number"
              placeholder="Thickness"
              value={newLineItem.dimensions.thickness}
              onChange={(e) =>
                setNewLineItem((prev) => ({
                  ...prev,
                  dimensions: { ...prev.dimensions, thickness: e.target.value },
                }))
              }
            />
          </>
        );
      default:
        return null;
    }
  };

  return (
    <div>
      <h1>Order Details</h1>
      {order && (
        <div>
          <p>Order ID: {order.orderId}</p>
          <p>Creator: {order.creator}</p>
        </div>
      )}

      <h2>Add New Line Item</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Line Item ID"
          value={newLineItem.lineItemId}
          onChange={(e) =>
            setNewLineItem((prev) => ({ ...prev, lineItemId: e.target.value }))
          }
          required
        />
        <select
  name="shape"
  value={newLineItem.shape}
  onChange={handleDropdownChange}
  onClick={fetchShapesOnClick} // Trigger fetching shapes on dropdown click
>
  <option value="">Select Shape</option>
  {shapes.map((shape) => (
    <option key={shape._id} value={shape.shapeId}>
      {shape.shapeId}
    </option>
  ))}
</select>

        <select
          name="grade"
          value={newLineItem.grade}
          onChange={(e) =>
            setNewLineItem((prev) => ({ ...prev, grade: e.target.value }))
          }
        >
          <option value="">Select Grade</option>
          {grades.map((grade) => (
            <option key={grade.grade} value={grade.grade}>
              {grade.grade}
            </option>
          ))}
        </select>

        {renderDimensionFields()}

        <Select
          name="processIds"
          options={processes.map((p) => ({ value: p.processId, label: p.processId }))}
          isMulti
          onChange={handleDropdownChange}
        />

        <Select
          name="vendorIds"
          options={vendors.map((v) => ({ value: v.vendorId, label: v.vendorId }))}
          isMulti
          onChange={handleDropdownChange}
        />

        <button type="submit">Add Line Item</button>
      </form>
      {message && <p>{message}</p>}
    </div>
  );
};

export default OrderDetails;
