import React, { useState, useEffect, useMemo } from 'react';
import { useTable, useSortBy, useFilters } from '@tanstack/react-table';
import { fetchExecutionDetailsGrouped } from '../services/api';
import ExecutionEditPopup from './ExecutionEditPopup';

const ExecutionDetailsPage = () => {
  const [executions, setExecutions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedExecution, setSelectedExecution] = useState(null);

  useEffect(() => {
    const loadExecutions = async () => {
      try {
        const data = await fetchExecutionDetailsGrouped();
        setExecutions(data);
      } catch (err) {
        console.error('âŒ Error fetching execution details:', err);
      } finally {
        setLoading(false);
      }
    };

    loadExecutions();
  }, []);

  const handleOpenPopup = (execution) => {
    setSelectedExecution(execution);
  };

  const handleClosePopup = () => {
    setSelectedExecution(null);
  };

  const handleSaveChanges = (updatedData) => {
    setExecutions((prev) =>
      prev.map((exec) => (exec._id === selectedExecution._id ? { ...exec, ...updatedData } : exec))
    );
  };

  const columns = useMemo(
    () => [
      { Header: 'Process', accessor: 'processInfo.name' },
      { Header: 'Vendor', accessor: 'vendorInfo.name' },
      { Header: 'Line Item', accessor: 'lineItemId' },
      { Header: 'Order', accessor: 'orderId' },
      { Header: 'Weight Sent', accessor: 'outWeight' },
      { Header: 'Actual Weight Sent', accessor: 'actualWeightSent' },
      { Header: 'Weight Received', accessor: 'inWeight' },
      { Header: 'Actual Weight Received', accessor: 'actualWeightReceived' },
      { Header: 'Status', accessor: 'status' },
      {
        Header: 'Actions',
        id: 'actions',
        Cell: ({ row }) => (
          <button onClick={() => handleOpenPopup(row.original)}>Edit</button>
        ),
      },
    ],
    []
  );

  const tableInstance = useTable(
    { columns, data: executions },
    useFilters,
    useSortBy
  );

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = tableInstance;

  return (
    <div className="container">
      <h2>Execution Management</h2>
      {loading ? (
        <p>Loading execution details...</p>
      ) : (
        <table {...getTableProps()} border="1">
          <thead>
            {headerGroups.map((headerGroup) => (
              <tr {...headerGroup.getHeaderGroupProps()}>
                {headerGroup.headers.map((column) => (
                  <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                    {column.render('Header')}
                    {column.isSorted ? (column.isSortedDesc ? ' ðŸ”½' : ' ðŸ”¼') : ''}
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody {...getTableBodyProps()}>
            {rows.map((row) => {
              prepareRow(row);
              return (
                <tr {...row.getRowProps()}>
                  {row.cells.map((cell) => (
                    <td {...cell.getCellProps()}>{cell.render('Cell')}</td>
                  ))}
                </tr>
              );
            })}
          </tbody>
        </table>
      )}

      {selectedExecution && (
        <ExecutionEditPopup
          execution={selectedExecution}
          onClose={handleClosePopup}
          onSave={handleSaveChanges}
        />
      )}
    </div>
  );
};

export default ExecutionDetailsPage;
