import React, { useState } from 'react';

const EditPopup = ({ lineItem, onClose, onSave }) => {
  const [updatedLineItem, setUpdatedLineItem] = useState(lineItem);

  const handleSave = () => {
    onSave(updatedLineItem); // Call the save function passed as a prop
    onClose(); // Close the popup
  };

  return (
    <div className="popup">
      <div className="popup-content">
        <h3>Edit Line Item</h3>
        <div>
          <label>Processes:</label>
          <input
            type="text"
            value={updatedLineItem.processIds.join(', ')}
            onChange={(e) =>
              setUpdatedLineItem((prev) => ({
                ...prev,
                processIds: e.target.value.split(',').map((val) => val.trim()),
              }))
            }
          />
        </div>
        <div>
          <label>Vendors:</label>
          <input
            type="text"
            value={updatedLineItem.vendorIds.join(', ')}
            onChange={(e) =>
              setUpdatedLineItem((prev) => ({
                ...prev,
                vendorIds: e.target.value.split(',').map((val) => val.trim()),
              }))
            }
          />
        </div>
        <button onClick={() => onSave(updatedLineItem)}>Save</button>
        <button onClick={onClose}>Cancel</button>
      </div>
    </div>
  );
};

export default EditPopup;
