import React, { useState, useEffect, useMemo } from 'react';
import { useReactTable, getCoreRowModel, getSortedRowModel, flexRender } from '@tanstack/react-table';
import { fetchExecutionDetailsGrouped } from '../services/api';
import ExecutionEditPopup from './ExecutionEditPopup';

const ExecutionDetailsPage = () => {
  const [executions, setExecutions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedExecution, setSelectedExecution] = useState(null);
  const [sorting, setSorting] = useState([]); // Sorting state

  useEffect(() => {
    const loadExecutions = async () => {
      try {
        const data = await fetchExecutionDetailsGrouped();
        console.log('âœ… API Response:', JSON.stringify(data, null, 2)); // Print full response
        setExecutions(data);
      } catch (err) {
        console.error('âŒ Error fetching execution details:', err);
      } finally {
        setLoading(false);
      }
    };

    loadExecutions();
  }, []);


  const handleOpenPopup = (execution) => {
    setSelectedExecution(execution);
  };

  const handleClosePopup = () => {
    setSelectedExecution(null);
  };

  const handleSaveChanges = (updatedData) => {
    setExecutions((prev) =>
      prev.map((exec) => (exec._id === selectedExecution._id ? { ...exec, ...updatedData } : exec))
    );
  };

  // Define table columns
  const columns = useMemo(() => [
    { accessorKey: 'processInfo.name', header: 'Process' },
    { accessorKey: 'vendorInfo.name', header: 'Vendor' },
    { accessorKey: 'lineItemId', header: 'Line Item' },
    { accessorKey: 'orderId', header: 'Order' },
    { accessorKey: 'outWeight', header: 'Weight Sent' },
    { accessorKey: 'actualWeightSent', header: 'Actual Weight Sent' },
    { accessorKey: 'inWeight', header: 'Weight Received' },
    { accessorKey: 'actualWeightReceived', header: 'Actual Weight Received' },
    { accessorKey: 'status', header: 'Status' },
    {
      accessorKey: 'actions',
      header: 'Actions',
      cell: ({ row }) => (
        <button onClick={() => handleOpenPopup(row.original)}>Edit</button>
      ),
    },
  ], []);

  // Initialize TanStack Table
  const table = useReactTable({
    data: executions,
    columns,
    getCoreRowModel: getCoreRowModel(),
    getSortedRowModel: getSortedRowModel(),
    state: {
      sorting,
    },
    onSortingChange: setSorting,
  });

  return (
    <div className="container">
      <h2>Execution Management</h2>
      {loading ? (
        <p>Loading execution details...</p>
      ) : (
        <table border="1">
          <thead>
            {table.getHeaderGroups().map(headerGroup => (
              <tr key={headerGroup.id}>
                {headerGroup.headers.map(header => (
                  <th key={header.id} onClick={header.column.getToggleSortingHandler()} style={{ cursor: 'pointer' }}>
                    {flexRender(header.column.columnDef.header, header.getContext())}
                    {header.column.getIsSorted() === 'asc' ? ' ðŸ”¼' : header.column.getIsSorted() === 'desc' ? ' ðŸ”½' : ''}
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody>
            {table.getRowModel().rows.map(row => (
              <tr key={row.id}>
                {row.getVisibleCells().map(cell => (
                  <td key={cell.id}>
                    {flexRender(cell.column.columnDef.cell, cell.getContext())}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {selectedExecution && (
        <ExecutionEditPopup
          execution={selectedExecution}
          onClose={handleClosePopup}
          onSave={handleSaveChanges}
        />
      )}
    </div>
  );
};

export default ExecutionDetailsPage;
