import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { fetchOrders, createOrder } from '../services/api';

const OrdersPage = () => {
  const [orders, setOrders] = useState([]);
  const [newOrder, setNewOrder] = useState({ orderId: '', creator: '', responsible: '', company: '' });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate(); // Use navigate for redirection

  // Fetch orders
  useEffect(() => {
    const loadOrders = async () => {
      try {
        const { data } = await fetchOrders();
        setOrders(data);
      } catch (err) {
        setError('Failed to fetch orders.');
      }
    };
    loadOrders();
  }, []);

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewOrder((prev) => ({ ...prev, [name]: value }));
  };

  // Handle order creation
  const handleCreateOrder = async () => {
    setLoading(true);
    try {
      const { data } = await createOrder(newOrder);
      setOrders((prev) => [...prev, data]);
      setNewOrder({ orderId: '', creator: '', responsible: '', company: '' });
      navigate(`/orders/${data.orderId}`); // Redirect to OrderDetailsPage
    } catch (err) {
      setError('Failed to create order.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <h1>Orders</h1>
      {error && <p style={{ color: 'red' }}>{error}</p>}
      {loading && <p>Loading...</p>}
      <ul>
        {orders.map((order) => (
          <li key={order.orderId}>
            <Link to={`/orders/${order.orderId}`}>{order.orderId}</Link> - {order.creator} - {order.responsible}
          </li>
        ))}
      </ul>

      <h2>Create Order</h2>
<form
  onSubmit={(e) => {
    e.preventDefault();
    handleCreateOrder();
  }}
>
  <input
    type="text"
    name="creator"
    placeholder="Creator"
    value={newOrder.creator}
    onChange={handleInputChange}
    required
  />
  <input
    type="text"
    name="responsible"
    placeholder="Responsible"
    value={newOrder.responsible}
    onChange={handleInputChange}
    required
  />
  <input
    type="text"
    name="company"
    placeholder="Company"
    value={newOrder.company}
    onChange={handleInputChange}
    required
  />
  <button type="submit" disabled={loading}>
    {loading ? 'Creating...' : 'Create Order'}
  </button>
</form>
    </div>
  );

};




export default OrdersPage;
