// File: src/services/api.js
import axios from 'axios';

const API = axios.create({
  baseURL: 'http://localhost:5045/api',
});

export const fetchOrders = () => API.get('/orders');
export const createOrder = async (order) => {
  try {
    const response = await API.post('/orders', {
      creator: order.creator,
      responsible: order.responsible,
      company: order.company,
    });
    return response.data;
  } catch (error) {
    console.error('Error creating order:', error.message);
    throw error;
  }
};
export const fetchOrder = (orderId) => API.get(`/orders/${orderId}`);
export const fetchLineItems = (orderId) => API.get(`/lineitems/${orderId}`);

export const fetchProcesses = async () => {
  try {
    const response = await API.get('/processes');
    console.log('Processes API response:', response.data); // Log response
    return response.data;
  } catch (error) {
    console.error('Error fetching shapes from API:', error.message);
    throw error;
  }
};
export const fetchVendors = async () => {
  try {
    const response = await API.get('/vendors');
    console.log('Vendors API response:', response.data); // Log response
    return response.data;
  } catch (error) {
    console.error('Error fetching shapes from API:', error.message);
    throw error;
  }
};
export const fetchShapes = async () => {
  try {
    const response = await API.get('/shapes');
    console.log('Shapes API response:', response.data); // Log response
    return response.data;
  } catch (error) {
    console.error('Error fetching shapes from API:', error.message);
    throw error;
  }
};

export const fetchGrades = async () => {
  try {
    const response = await API.get('/grades');
    console.log('Grades API response:', response.data); // Log response
    return response.data;
  } catch (error) {
    console.error('Error fetching shapes from API:', error.message);
    throw error;
  }
};
export const createVendor = (vendor) => API.post('/vendors', vendor);
export const createProcess = (process) => API.post('/processes', process);
export const createShape = (shape) => API.post('/shapes', shape);
export const createGrade = (grade) => API.post('/grades', grade);
export const createLineItem = async (lineItem) => {
  try {
    const response = await API.post('/lineitems', lineItem); // Send the full lineItem object
    return response.data;
  } catch (error) {
    console.error('Error creating line item:', error.response?.data || error.message);
    throw error;
  }
};

export default API;
