import React from 'react';
//import './Popup.css'; // Import CSS styles

const LineItemDetailsPopup = ({ lineItem, onClose }) => {
  if (!lineItem) return null;

  return (
    <div className="popup-overlay" onClick={onClose}>
      <div className="popup-content" onClick={(e) => e.stopPropagation()}>
        <h2>Material Sizes for {lineItem.lineItemId}</h2>
        <table>
          <thead>
            <tr>
              <th>Material Size</th>
              <th>Status</th>
              <th>Weight (kg)</th>
            </tr>
          </thead>
          <tbody>
            {lineItem.materialSizes.map((material, index) => (
              <tr key={index}>
                <td>{material.size}</td>
                <td>{material.status}</td>
                <td>{material.weight}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <button className="close-btn" onClick={onClose}>Close</button>
      </div>
    </div>
  );
};

export default LineItemDetailsPopup;
