import React, { useState, useEffect } from 'react';
import { fetchExecutionDetailsGrouped } from '../services/api';
import ExecutionEditPopup from './ExecutionEditPopup';

const ExecutionDetailsPage = () => {
  const [executions, setExecutions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedExecution, setSelectedExecution] = useState(null);

  useEffect(() => {
    const loadExecutions = async () => {
      try {
        const data = await fetchExecutionDetailsGrouped();
        setExecutions(data);
      } catch (err) {
        console.error('❌ Error fetching execution details:', err);
      } finally {
        setLoading(false);
      }
    };

    loadExecutions();
  }, []);

  const handleOpenPopup = (execution) => {
    setSelectedExecution(execution);
  };

  const handleClosePopup = () => {
    setSelectedExecution(null);
  };

  const handleSaveChanges = (updatedData) => {
    setExecutions((prev) =>
      prev.map((exec) => (exec._id === selectedExecution._id ? { ...exec, ...updatedData } : exec))
    );
  };

  return (
    <div className="container">
      <h2>Execution Management</h2>
      {loading ? (
        <p>Loading execution details...</p>
      ) : (
        <table border="1">
          <thead>
            <tr>
              <th>Process</th>
              <th>Vendor</th>
              <th>Line Item</th>
              <th>Order</th>
              <th>Weight Sent</th>
              <th>Actual Weight Sent</th>
              <th>Weight Received</th>
              <th>Actual Weight Received</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {executions.map((exec) => (
              <tr key={exec._id}>
                <td>{exec.processInfo?.name || exec.processId}</td>
                <td>{exec.vendorInfo?.name || exec.vendorId}</td>
                <td>{exec.lineItemId}</td>
                <td>{exec.orderId}</td>
                <td>{exec.outWeight}</td>
                <td>{exec.actualWeightSent || '-'}</td>
                <td>{exec.inWeight}</td>
                <td>{exec.actualWeightReceived || '-'}</td>
                <td>{exec.status}</td>
                <td>
                  <button onClick={() => handleOpenPopup(exec)}>Edit</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      {selectedExecution && (
        <ExecutionEditPopup
          execution={selectedExecution}
          onClose={handleClosePopup}
          onSave={handleSaveChanges}
        />
      )}
    </div>
  );
};

export default ExecutionDetailsPage;
