import React, { useState, useEffect } from 'react';
import { fetchVendors, fetchProcesses, fetchLineItemProcesses, reorderLineItemProcesses, createLineItemProcess,deleteLineItemProcess } from '../services/api';
import { DndContext, closestCenter } from '@dnd-kit/core';
import {
  arrayMove,
  SortableContext,
  useSortable,
  sortableKeyboardCoordinates,
} from '@dnd-kit/sortable';
import { KeyboardSensor, PointerSensor, useSensor, useSensors } from '@dnd-kit/core';
import { CSS } from '@dnd-kit/utilities';

const EditPopup = ({ lineItem, onClose, onSave }) => {
  const [vendors, setVendors] = useState([]); // Vendor list
  const [processes, setProcesses] = useState([]); // Process list
  const [lineItemProcesses, setLineItemProcesses] = useState([]); // Existing LineItemProcesses
  const [newProcess, setNewProcess] = useState({ processId: '', vendorId: '' });
  const [draggedProcess, setDraggedProcess] = useState(null);

  useEffect(() => {
    // Fetch initial data for vendors, processes, and line item processes
    const loadInitialData = async () => {
      try {
        const encodedLineItemId = encodeURIComponent(lineItem.lineItemId); // Encode lineItemId
        console.log('editpopup: Encoded lineItemId:', encodedLineItemId);

        // Fetch vendors
        const vendorData = await fetchVendors();
        console.log('editpopup: Fetched vendors:', vendorData);
        setVendors(vendorData);

        // Fetch processes
        const processData = await fetchProcesses();
        console.log('editpopup: Fetched processes:', processData);
        setProcesses(processData);

        // Fetch line item processes
        const lineItemProcessData = await fetchLineItemProcesses(encodedLineItemId);
        console.log('editpopup: Fetched line item processes:', lineItemProcessData);
        setLineItemProcesses(
          lineItemProcessData.map((process, index) => ({
            ...process,
            _id: process._id || `temp-${Math.random()}`,
          }))
        );
      } catch (err) {
        console.error('editpopup: Error loading initial data:', err.message);
      }
    };

    if (lineItem.lineItemId) {
      loadInitialData(); // Fetch data if lineItemId is available
    }
  }, [lineItem.lineItemId]);

  // Sensors for drag-and-drop
  const sensors = useSensors(
    useSensor(PointerSensor),
    useSensor(KeyboardSensor, {
      coordinateGetter: sortableKeyboardCoordinates,
    })
  );

  // Sortable item component
  const SortableItem = ({ id, process }) => {
    const { attributes, listeners, setNodeRef, transform, transition } = useSortable({ id });

    const style = {
      transform: CSS.Transform.toString(transform),
      transition,
      padding: '10px',
      margin: '5px 0',
      background: '#f8f8f8',
      border: '1px solid #ddd',
      borderRadius: '4px',
      display: 'flex',
      justifyContent: 'space-between',
    };

    return (
      <div ref={setNodeRef} style={style} {...attributes} {...listeners}>
        <span>
          Sequence: {process.sequence}, Process: {process.processId}, Vendor: {process.vendorId}
        </span>
        <button
          onClick={async () => {
            try {
              await deleteLineItemProcess(id); // Remove from backend
              setLineItemProcesses((prev) => prev.filter((item) => item._id !== id)); // Update state
              console.log(`Removed process with id ${id}`);
            } catch (err) {
              console.error('Error removing process:', err.message);
            }
          }}
          style={{
            marginLeft: '10px',
            color: 'red',
            border: 'none',
            background: 'none',
          }}
        >
          Remove
        </button>
      </div>
    );
  };


  const handleDragEnd = (event) => {
    const { active, over } = event;

    // Ensure both active and over are defined
    if (!active || !over) {
      console.warn('Drag event missing active or over:', { active, over });
      return;
    }

    // Check if active and over have valid IDs
    if (!active.id || !over.id) {
      console.warn('Active or Over missing ID:', { active, over });
      return;
    }

    if (active.id !== over.id) {
      setLineItemProcesses((items) => {
        const oldIndex = items.findIndex((item) => item._id === active.id);
        const newIndex = items.findIndex((item) => item._id === over.id);

        if (oldIndex === -1 || newIndex === -1) {
          console.warn('Invalid indices during drag-and-drop:', { oldIndex, newIndex });
          return items;
        }

        const updatedItems = arrayMove(items, oldIndex, newIndex);

        // Update sequence numbers after reordering
        return updatedItems.map((item, index) => ({
          ...item,
          sequence: index + 1,
        }));
      });
    }
  };


  const handleAddProcess = async () => {
    if (newProcess.processId && newProcess.vendorId) {
      const newEntry = {
        lineItemId: lineItem.lineItemId, // Pass the lineItemId for association
        processId: newProcess.processId,
        vendorId: newProcess.vendorId,
        sequence: lineItemProcesses.length + 1,
      };
      console.log('editpopup: New process to add:', newEntry);

      try {
        await createLineItemProcess(newEntry); // Save to backend
        console.log('editpopup: Added new process');

        // Refetch processes to ensure data is consistent
        const updatedProcesses = await fetchLineItemProcesses(encodeURIComponent(lineItem.lineItemId));
        setLineItemProcesses(
          updatedProcesses.map((process, index) => ({
            ...process,
            _id: process._id || `process-${index}`,
          }))
        );
        setNewProcess({ processId: '', vendorId: '' }); // Reset the input fields
      } catch (err) {
        console.error('editpopup: Error adding new process:', err.message);
      }
    }
  };


  const handleSave = async () => {
    try {
      const encodedLineItemId = encodeURIComponent(lineItem.lineItemId); // Encode lineItemId
      console.log(`editpopup: Encoded lineItemId for save: ${encodedLineItemId}`);

      // Save reordered processes to backend
      const reorderedProcessesPayload = lineItemProcesses.map((process) => ({
        lineItemProcessId: process._id, // Ensure this matches the backend field
        sequence: process.sequence,
      }));
      await reorderLineItemProcesses(encodedLineItemId, reorderedProcessesPayload);

      console.log('editpopup: Saved reordered processes:', lineItemProcesses);

      // Refetch updated processes to ensure the latest data is shown
      const updatedProcesses = await fetchLineItemProcesses(encodedLineItemId);
      setLineItemProcesses(
        updatedProcesses.map((process, index) => ({
          ...process,
          _id: process._id || `process-${index}`,
        }))
      );

      const processIds = updatedProcesses.map((item) => item.processId);
      const vendorIds = updatedProcesses.map((item) => item.vendorId);

      console.log('Saving with:', { processIds, vendorIds, lineItemProcesses });
      onSave({ ...lineItem, processIds, vendorIds, lineItemProcesses: updatedProcesses });
    } catch (err) {
      console.error('editpopup: Error saving processes:', err.message);
    }
  };


  return (
    <div className="popup">
      <div className="popup-content">
        <h2>Edit Line Item</h2>

        <DndContext
          sensors={sensors}
          collisionDetection={closestCenter}
          onDragEnd={handleDragEnd}
        >
<SortableContext items={lineItemProcesses.map((item) => item._id)}>
  {lineItemProcesses.map((process) => (
    <SortableItem key={process._id} id={process._id} process={process} />
  ))}
</SortableContext>

        </DndContext>

        <div style={{ marginTop: '20px' }}>
          <h3>Add New Process</h3>
          <select
            value={newProcess.processId}
            onChange={(e) => setNewProcess((prev) => ({ ...prev, processId: e.target.value }))}
            style={{ marginRight: '10px' }}
          >
            <option value="">Select Process</option>
            {processes.map((process) => (
              <option key={process.processId} value={process.processId}>
                {process.processId}
              </option>
            ))}
          </select>
          <select
            value={newProcess.vendorId}
            onChange={(e) => setNewProcess((prev) => ({ ...prev, vendorId: e.target.value }))}
            style={{ marginRight: '10px' }}
          >
            <option value="">Select Vendor</option>
            {vendors.map((vendor) => (
              <option key={vendor.vendorId} value={vendor.vendorId}>
                {vendor.vendorId}
              </option>
            ))}
          </select>
          <button onClick={handleAddProcess} style={{ padding: '5px 10px', background: 'blue', color: 'white' }}>
            Add
          </button>
        </div>

        <div style={{ marginTop: '20px' }}>
          <button onClick={handleSave} style={{ padding: '5px 10px', background: 'green', color: 'white' }}>
            Save
          </button>
          <button onClick={onClose} style={{ padding: '5px 10px', background: 'red', color: 'white' }}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default EditPopup;
