import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { fetchOrders, createOrder, deleteOrder, updateOrder, fetchNextOrderId } from '../services/api';

const OrdersPage = () => {
  const [orders, setOrders] = useState([]); // Ensure orders is initialized as an empty array
  const [nextOrderId, setNextOrderId] = useState('');
  const [newOrder, setNewOrder] = useState({ creator: '', responsible: '', company: '' });
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    const loadOrders = async () => {
      try {
        const data = await fetchOrders();
        setOrders(data || []); // Ensure data is always an array
      } catch (err) {
        console.error('Error fetching orders:', err.message);
        setError('Failed to fetch orders.');
      }
    };

    const loadNextOrderId = async () => {
      try {
        const id = await fetchNextOrderId();
        setNextOrderId(id);
      } catch (err) {
        console.error('Error fetching next order ID:', err.message);
        setError('Failed to fetch next order ID.');
      }
    };

    loadOrders();
    loadNextOrderId();
  }, []);

  const handleCreateOrder = async () => {
    setLoading(true);
    try {
      const createdOrder = await createOrder(newOrder);
      setOrders((prev) => [...prev, createdOrder]);
      navigate(`/orders/${createdOrder.orderId}`);
    } catch (err) {
      console.error('Error creating order:', err.message);
      setError('Failed to create order.');
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteOrder = async (orderId) => {
    try {
      await deleteOrder(orderId);
      setOrders((prev) => prev.filter((order) => order.orderId !== orderId));
    } catch (err) {
      console.error('Error deleting order:', err.message);
      setError('Failed to delete order.');
    }
  };

  if (error) {
    return <p style={{ color: 'red' }}>{error}</p>;
  }

  if (loading) {
    return <p>Loading...</p>;
  }

  return (
    <div>
      <h1>Orders</h1>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        <table border="1" style={{ width: '100%', textAlign: 'left', marginBottom: '20px' }}>
          <thead>
            <tr>
              <th>Order ID</th>
              <th>Creator</th>
              <th>Responsible</th>
              <th>Company</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {orders.map((order) => (
              <tr key={order.orderId}>
                <td>{order.orderId}</td>
                <td>{order.creator}</td>
                <td>{order.responsible}</td>
                <td>{order.company}</td>
                <td>
                  <button onClick={() => navigate(`/orders/${order.orderId}`)}>View</button>
                  <button onClick={() => handleDeleteOrder(order.orderId)}>Delete</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}

      <h2>Create Order</h2>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          handleCreateOrder();
        }}
      >
        <div>
          <label htmlFor="orderId">Next Order ID:</label>
          <input type="text" id="orderId" value={nextOrderId} readOnly />
        </div>
        <input
          type="text"
          name="creator"
          placeholder="Creator"
          value={newOrder.creator}
          onChange={(e) => setNewOrder((prev) => ({ ...prev, creator: e.target.value }))}
          required
        />
        <input
          type="text"
          name="responsible"
          placeholder="Responsible"
          value={newOrder.responsible}
          onChange={(e) => setNewOrder((prev) => ({ ...prev, responsible: e.target.value }))}
          required
        />
        <input
          type="text"
          name="company"
          placeholder="Company"
          value={newOrder.company}
          onChange={(e) => setNewOrder((prev) => ({ ...prev, company: e.target.value }))}
          required
        />
        <button type="submit" disabled={loading}>
          {loading ? 'Creating...' : 'Create Order'}
        </button>
      </form>
    </div>
  );
};

export default OrdersPage;
