import React, { useState, useEffect } from 'react';
import { fetchVendors, fetchProcesses, fetchLineItemProcesses, reorderLineItemProcesses, createLineItemProcess } from '../services/api';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

const EditPopup = ({ lineItem, onClose, onSave }) => {
  const [vendors, setVendors] = useState([]); // Vendor list
  const [processes, setProcesses] = useState([]); // Process list
  const [lineItemProcesses, setLineItemProcesses] = useState([]); // Existing LineItemProcesses
  const [newProcess, setNewProcess] = useState({ processId: '', vendorId: '' });

  useEffect(() => {
    // Fetch initial data for vendors, processes, and line item processes
    const loadInitialData = async () => {
      try {
        const encodedLineItemId = lineItem.lineItemId;
        console.log('editpopup: Encoded lineItemId:', encodedLineItemId);

        const processes = await fetchLineItemProcesses(encodedLineItemId);
        if (processes.length === 0) {
          console.log('editpopup: No processes found for lineItemId:', lineItem.lineItemId);
          setLineItemProcesses([]); // Clear existing processes
          return;
        }

        console.log('editpopup: Fetched processes:', processes);
        setLineItemProcesses(processes);
      } catch (err) {
        console.error('editpopup: Error fetching processes:', err.message);
      }
    };


    if (lineItem.lineItemId) {
      loadInitialData(); // Fetch data if lineItemId is available
    }
  }, [lineItem.lineItemId]);

  // Handle drag-and-drop reordering
  const handleDragEnd = (result) => {
    if (!result.destination) return;

    const reordered = Array.from(lineItemProcesses);
    const [moved] = reordered.splice(result.source.index, 1);
    reordered.splice(result.destination.index, 0, moved);

    const updatedProcesses = reordered.map((item, index) => ({
      ...item,
      sequence: index + 1, // Update sequence numbers after reordering
    }));
    console.log('editpopup: Updated sequence after drag-and-drop:', updatedProcesses);
    setLineItemProcesses(updatedProcesses);
  };

  // Handle adding a new process
  const handleAddProcess = async () => {
    if (newProcess.processId && newProcess.vendorId) {
      const newEntry = {
        lineItemId: lineItem.lineItemId, // Pass the lineItemId for association
        processId: newProcess.processId,
        vendorId: newProcess.vendorId,
        sequence: lineItemProcesses.length + 1,
      };
      console.log('editpopup: New process to add:', newEntry);

      try {
        const addedProcess = await createLineItemProcess(newEntry); // Save to backend
        console.log('editpopup: Added new process:', addedProcess);

        setLineItemProcesses((prev) => [...prev, addedProcess]); // Update the list in state
        setNewProcess({ processId: '', vendorId: '' }); // Reset the input fields
      } catch (err) {
        console.error('editpopup: Error adding new process:', err.message);
      }
    }
  };

  // Save the reordered and updated processes
  const handleSave = async () => {
    try {
      const encodedLineItemId = encodeURIComponent(lineItem.lineItemId); // Encode lineItemId
      console.log(`editpopup: Encoded lineItemId for save: ${encodedLineItemId}`);

      await reorderLineItemProcesses(encodedLineItemId, lineItemProcesses); // Save reordered list to backend
      console.log('editpopup: Saved reordered processes:', lineItemProcesses);

      onSave(lineItemProcesses); // Notify parent component of changes
    } catch (err) {
      console.error('editpopup: Error saving processes:', err.message);
    }
  };

 return (
  <div className="popup">
    <div className="popup-content">
      <h2>Edit Line Item</h2>

      {/* Conditional Rendering for Processes */}
      <div>
        {lineItemProcesses.length === 0 ? (
          <p>No processes found for this line item.</p>
        ) : (
          <DragDropContext onDragEnd={handleDragEnd}>
            <Droppable droppableId="lineItemProcesses">
              {(provided) => (
                <ul {...provided.droppableProps} ref={provided.innerRef} style={{ listStyleType: 'none', padding: 0 }}>
                  {lineItemProcesses.map((item, index) => (
                    <Draggable key={item._id} draggableId={item._id} index={index}>
                      {(provided) => (
                        <li
                          ref={provided.innerRef}
                          {...provided.draggableProps}
                          {...provided.dragHandleProps}
                          style={{
                            display: 'flex',
                            justifyContent: 'space-between',
                            padding: '10px',
                            margin: '5px 0',
                            background: '#f8f8f8',
                            border: '1px solid #ddd',
                            borderRadius: '4px',
                          }}
                        >
                          <span>
                            Sequence: {item.sequence}, Process: {item.processId}, Vendor: {item.vendorId}
                          </span>
                          <button
                            onClick={() =>
                              setLineItemProcesses((prev) =>
                                prev.filter((process) => process._id !== item._id)
                              )
                            }
                            style={{
                              marginLeft: '10px',
                              color: 'red',
                              border: 'none',
                              background: 'none',
                            }}
                          >
                            Remove
                          </button>
                        </li>
                      )}
                    </Draggable>
                  ))}
                  {provided.placeholder}
                </ul>
              )}
            </Droppable>
          </DragDropContext>
        )}
      </div>

      {/* Add New Process Section */}
      <div style={{ marginTop: '20px' }}>
        <h3>Add New Process</h3>
        <select
          value={newProcess.processId}
          onChange={(e) => setNewProcess((prev) => ({ ...prev, processId: e.target.value }))}
          style={{ marginRight: '10px' }}
        >
          <option value="">Select Process</option>
          {processes.map((process) => (
            <option key={process.processId} value={process.processId}>
              {process.processId}
            </option>
          ))}
        </select>
        <select
          value={newProcess.vendorId}
          onChange={(e) => setNewProcess((prev) => ({ ...prev, vendorId: e.target.value }))}
          style={{ marginRight: '10px' }}
        >
          <option value="">Select Vendor</option>
          {vendors.map((vendor) => (
            <option key={vendor.vendorId} value={vendor.vendorId}>
              {vendor.vendorId}
            </option>
          ))}
        </select>
        <button onClick={handleAddProcess} style={{ padding: '5px 10px', background: 'blue', color: 'white' }}>
          Add
        </button>
      </div>

      {/* Save and Cancel Buttons */}
      <div style={{ marginTop: '20px' }}>
        <button onClick={handleSave} style={{ padding: '5px 10px', background: 'green', color: 'white' }}>
          Save
        </button>
        <button onClick={onClose} style={{ padding: '5px 10px', background: 'red', color: 'white' }}>
          Cancel
        </button>
      </div>
    </div>
  </div>
);
};

export default EditPopup;
